+    height: auto;
+    gap: 8px;
+}
+
+.todo-list-section ul li {
+    width: 80%;
+    height: 120px;
+    padding: 24px 32px 24px 32px;
+    margin-top: 5px;
+    border-radius: 16px;
+    border: 1px solid #EEEEEE;
+    list-style: none;
+    display: flex;
+    flex-direction: column;
+    align-items: flex-start;
+    justify-content: center;
