+        max-width: 80rem;
+        padding: 2.4rem 1.6rem;
+    }
+    h1 {
+        font-size: 3.5rem;
+        color: #5eb7f6;
+    }
+
+    .remove-done-task-btn {
+        font-size: 1.8rem;
+        padding: 0 1rem;
+    }
