+    width: 100%;
+    height: 4rem;
+    color: #fff;
+    background-color: #2D70FD;
+    border-radius: 4px;
+    font-size: 2.4rem;
+    cursor: pointer;
+    border: none;
+}
+
+.add-tarefa-btn:hover {
+    opacity: 0.8;
+}
+
+
+.task-details{
+    display: flex;
+    align-items: flex-start;
+    justify-content: center;
+    flex-direction: column;
+    gap: 5px;
+}
+
+.task-text{
+    font-family: 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode';
+    font-size: 16px;
+    font-weight: 500;
+    line-height: 17.6px;
+    letter-spacing: 0.01em;
+    text-underline-position: from-font;
+    text-decoration-skip-ink: none;
+
+}
+
+.todo-item.completed .task-text {
+    text-decoration: line-through;
+    color: #bbbbbb;
+    font-family: 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode';
+    font-size: 1.5rem;
+    font-weight: 500;
+    line-height: 17.6px;
+    letter-spacing: 0.01em;
+    text-decoration-line: line-through;
+    text-underline-position: from-font;
+    text-decoration-skip-ink: none;
+
+}
+
+.todo-item.completed .task-completed-icon {
+    font-size: 1.6rem;
+    color: #fff;
+    align-self: center;
+    width: 32px;
+    height: 32px;
+    border-radius: 32px;
+    background: #00D8A7;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+}
+
+.task-label {
+    width: 67px;
+    height: 21px;
+    border-radius: 16px;
+    border: 1px 0px 0px 0px;
+    background: #FFFFFF;
+    border: 1px solid #b2bec3;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    color: #b2bec3;
+}
+
+.task-date {
+    font-size: 1.2rem;
+    color: #b2bec3;
+    margin-top: 0.4rem;
+}
+
+
+#todo-footer {
+    width: 100%;
+    display: flex;
+    justify-content: flex-end;
+    font-size: 1.6rem;
+    align-items: flex-end;
+    height: 50px;
+}
+
+@media (min-width: 768px) {
+    main {
+        max-width: 50rem;
+        padding: 2.4rem 1.6rem;
+    }
+
+    h1 {
+        color: #da5c3e;
+    }
+
+    .todo-list-section ul li {
+        font-size: 1.5rem;
+    }
+
+    .add-tarefa-btn {
+        width: 5rem;
+        height: 3.5rem;
+    }
+
+    #nome-tarefa {
+        font-size: 1.5rem;
+    }
+    .remove-done-task-btn {
+        font-size: 1.5rem;
+        padding: 0 1rem;
+    }
+}
+
+@media (min-width: 980px) {
+    main {
+        max-width: 80rem;
+        padding: 2.4rem 1.6rem;
+    }
+    h1 {
+        font-size: 3.5rem;
+        color: #5eb7f6;
+    }
+
+    .remove-done-task-btn {
+        font-size: 1.8rem;
+        padding: 0 1rem;
+    }
+}

